func TestFloor(t *testing.T) {
	for _, tc := range floorTests {
		t.Run(tc.input, func(t *testing.T) {
			output := FinalFloor(tc.input)
			if output != tc.expectedOutput {
				t.Errorf("Wrong floor: %v, expected: %v\n", output, tc.expectedOutput)
			}
		})
	}
}

